import React from "react";

function Main() {
  class TypeWriter {
    constructor(txtElement, words, wait = 3000) {
      this.txtElement = txtElement;
      this.words = words;
      this.txt = "";
      this.wordIndex = 0;
      this.wait = parseInt(wait, 8);
      this.type();
      this.isDeleting = false;
    }

    type() {
      // Current index of word
      const current = this.wordIndex % this.words.length;
      // Get full text of current word
      const fullTxt = this.words[current];

      // Check if deleting
      if (this.isDeleting) {
        // Remove char
        this.txt = fullTxt.substring(0, this.txt.length - 1);
      } else {
        // Add char
        this.txt = fullTxt.substring(0, this.txt.length + 1);
      }

      // Insert txt into element
      this.txtElement.innerHTML = `<span class="txt">${this.txt}</span>`;

      // change color for data-text
      this.txtElement.innerHTML = `<span class="txt" style="color: #e2000f;">${this.txt}</span>`;

      // Initial Type Speed
      let typeSpeed = 100;

      if (this.isDeleting) {
        typeSpeed /= 2;
      }

      // If word is complete
      if (!this.isDeleting && this.txt === fullTxt) {
        // Make pause at end
        typeSpeed = this.wait;
        // Set delete to true
        this.isDeleting = true;
      } else if (this.isDeleting && this.txt === "") {
        this.isDeleting = false;
        // Move to next word
        this.wordIndex++;
        // Pause before start typing
        typeSpeed = 300;
      }

      setTimeout(() => this.type(), typeSpeed);
    }
  }

  // Init On DOM Load
  document.addEventListener("DOMContentLoaded", init);

  // Init App
  function init() {
    const txtElement = document.querySelector(".txt-type");
    const words = JSON.parse(txtElement.getAttribute("data-words"));
    const wait = txtElement.getAttribute("data-wait");
    // Init TypeWriter
    new TypeWriter(txtElement, words, wait);
  }
  return (
    <section
      className="site-hero"
      style={{ background: "#808080" }}
      //   style={{ backgroundImage: "url(cdn/images/image_1.jpg)" }}
      id="section-home"
      data-stellar-background-ratio="0.5"
    >
      <div className="container">
        <div className="row intro-text align-items-center justify-content-center">
          <div className="col-md-10 text-center pt-5">
            <h1>
              Life Is Full Of
              <br />
              <span
                class="txt-type"
                data-wait="3000"
                data-words='["Love", "Hate", "Pain", "Joy", "Fun", "Adventures"]'
              ></span>
            </h1>
            <h1 className="site-heading site-animate">
              Hello, I'm <strong className="d-block">Charles Anderson</strong>
            </h1>
            <strong className="d-block text-white text-uppercase letter-spacing">
              and this is My Rezume
            </strong>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Main;
