import axios from "axios";
import React, { useState } from "react";

function Contact() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post("http://https://www.ceyhunergun.com.tr//send-email", {
        name,
        email,
        phone,
        message,
      });

      // Optionally, you can add logic here to handle a successful submission
      console.log("Email sent successfully");
    } catch (error) {
      console.error("Error sending email:", error);
    }
  };

  return (
    <section className="site-section" id="section-contact">
      <div className="container">
        <div className="row">
          <div className="col-md-12 mb-5">
            <div className="section-heading text-center">
              <h2>
                <strong>Get In Touch</strong>
              </h2>
            </div>
          </div>
          <div className="col-md-7 mb-5 mb-md-0">
            <form className="site-form" onSubmit={handleSubmit}>
              <h3 className="mb-5">Get In Touch</h3>
              <div className="form-group">
                <input
                  type="text"
                  className="form-control px-3 py-4"
                  placeholder="Name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </div>
              <div className="form-group">
                <input
                  type="email"
                  className="form-control px-3 py-4"
                  placeholder="Your Email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>
              <div className="form-group">
                <input
                  type="number"
                  className="form-control px-3 py-4"
                  placeholder="Your Phone"
                  value={phone}
                  onChange={(e) => setPhone(e.target.value)}
                />
              </div>
              <div className="form-group mb-5">
                <textarea
                  className="form-control px-3 py-4"
                  cols={30}
                  rows={10}
                  placeholder="Write a Message"
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                />
              </div>
              <div className="form-group">
                <input
                  type="submit"
                  className="btn btn-primary px-4 py-3"
                  defaultValue="Send Message"
                />
              </div>
            </form>
          </div>
          <div className="col-md-5 pl-md-5">
            <h3 className="mb-5">My Contact Details</h3>
            <ul className="site-contact-details">
              <li>
                <span className="text-uppercase" style={{ color: "#60e5ab" }}>
                  Email
                </span>
                ceyhunergun61@gmail.com
              </li>
              <li>
                <span className="text-uppercase" style={{ color: "#60e5ab" }}>
                  Phone
                </span>
                +90 538 311 5789
              </li>
              <li>
                <span className="text-uppercase" style={{ color: "#60e5ab" }}>
                  Address
                </span>
                Istanbul, Turkey
              </li>
            </ul>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Contact;
